import './App.css';
import Modal from 'react-modal'

function App() {
  const [isOpen, setIsOpen] = useState
  return (
    <div className="App">
      <button>Open model</button>
      <Modal isOpen={true}>
        <h2>Model title</h2>
        <p>Model Body</p>
      </Modal>
    </div>
  );
}

export default App;
