import { now } from '../utils'

export default class ObliviousSet {
  constructor (ttl = 1000 * 60) {
    this.ttl = ttl
    this.set = new Set()
    this.timeMap = new Map()
  }

  has (value) {
    return this.set.has(value)
  }

  add (value) {
    this.timeMap.set(value, now())
    this.set.add(value)
    this._removeTooOldValues()
  }

  clear () {
    this.set.clear()
    this.timeMap.clear()
  }

  _removeTooOldValues () {
    const olderThen = now() - this.ttl
    const iterator = this.set[Symbol.iterator]()

    while (true) {
      const value = iterator.next().value
      if (!value) return // no more elements
      const time = this.timeMap.get(value)
      if (time < olderThen) {
        this.timeMap.delete(value)
        this.set.delete(value)
      } else {
        // We reached a value that is not old enough
        return
      }
    }
  }
}

import ObliviousSet from '../ObliviousSet'

import {
  randomToken,
  microSeconds
} from '../../utils'

export const type = 'localStorage'

/**
 * Returns local storage instance
 */
export function getLocalStorage () {
  let localStorage
  if (typeof window === 'undefined') return null
  try {
    localStorage = window.localStorage
    localStorage = window['ie8-eventlistener/storage'] || window.localStorage
  } catch (e) {
    // New versions of Firefox throw a Security exception
    // if cookies are disabled. See
    // https://bugzilla.mozilla.org/show_bug.cgi?id=1028153
  }
  return localStorage
}

export function storageKey (channelName) {
  return channelName
}

/**
* writes the new message to the storage
* and fires the storage-event so other readers can find it
*/
export function postMessage (channelState, messageJson) {
  return new Promise(resolve => {
    const key = storageKey(channelState.channelName)
    const writeObj = {
      token: randomToken(),
      time: new Date().getTime(),
      data: messageJson,
      uuid: channelState.uuid
    }
    const value = JSON.stringify(writeObj)
    getLocalStorage().setItem(key, value)

    /**
       * StorageEvent does not fire the 'storage' event
       * in the window that changes the state of the local storage.
       * So we fire it manually
       */
    const ev = document.createEvent('Event')
    ev.initEvent('storage', true, true)
    ev.key = key
    ev.newValue = value
    window.dispatchEvent(ev)

    resolve()
  })
}

export function addStorageEventListener (channelName, fn) {
  const key = storageKey(channelName)
  const listener = ev => {
    if (ev.key === key) {
      fn(JSON.parse(ev.newValue))
    }
  }
  window.addEventListener('storage', listener)
  return listener
}
export function removeStorageEventListener (listener) {
  window.removeEventListener('storage', listener)
}

export function create (channelName, options = {}) {
  if (!canBeUsed()) {
    /* istanbul ignore next */
    throw new Error('❌ localStorage cannot be used.')
  }

  const uuid = randomToken()

  /**
     * eMIs
     * contains all messages that have been emitted before
     * @type {ObliviousSet}
     */
  const eMIs = new ObliviousSet(options.removeTimeout)

  const state = {
    channelName,
    uuid,
    eMIs // emittedMessagesIds
  }

  state.listener = addStorageEventListener(
    channelName,
    (msgObj) => {
      if (!state.messagesCallback) return // no listener
      if (msgObj.uuid === uuid) return // own message
      if (!msgObj.token || eMIs.has(msgObj.token)) return // already emitted
      if (msgObj.data.time && msgObj.data.time < state.messagesCallbackTime) return // too old

      eMIs.add(msgObj.token)
      state.messagesCallback(msgObj.data)
    }
  )

  return state
}

export function close (channelState) {
  removeStorageEventListener(channelState.listener)
}

export function onMessage (channelState, fn, time) {
  channelState.messagesCallbackTime = time
  channelState.messagesCallback = fn
}

export function canBeUsed () {
  const ls = getLocalStorage()

  if (!ls) return false

  try {
    const key = '__check'
    ls.setItem(key, 'works')
    ls.removeItem(key)
  } catch (e) {
    // Safari 10 in private mode will not allow write access to local
    // storage and fail with a QuotaExceededError. See
    // https://developer.mozilla.org/en-US/docs/Web/API/Web_Storage_API#Private_Browsing_Incognito_modes

    /* istanbul ignore next */
    return false
  }

  return true
}

/* istanbul ignore next */
export function averageResponseTime () {
  const defaultTime = 120
  const userAgent = navigator.userAgent.toLowerCase()
  if (userAgent.includes('safari') && !userAgent.includes('chrome')) {
    // safari is much slower so this time is higher
    return defaultTime * 2
  }
  return defaultTime
}

export default {
  create,
  close,
  onMessage,
  postMessage,
  canBeUsed,
  type,
  averageResponseTime,
  microSeconds
}

