      )
    }
    return _post(this, 'message', msg)
  }

  postInternal (msg) {
    return _post(this, 'internal', msg)
  }

  set onmessage (fn) {
    const time = this.method.microSeconds()
    const listenObj = {
      time,
      fn
    }
    _removeListenerObject(this, 'message', this._onMessageListener)
    if (fn && typeof fn === 'function') {
      this._onMessageListener = listenObj
      _addListenerObject(this, 'message', listenObj)
    } else {
      /* istanbul ignore next */
      this._onMessageListener = null
    }
  }

  /* istanbul ignore next */
  get onmessage () {
    return this._onMessageListener
  }

  addEventListener (type, fn) {
    const time = this.method.microSeconds()
    const listenObj = {
      time,
      fn
    }
    _addListenerObject(this, type, listenObj)
  }

  removeEventListener (type, fn) {
    const obj = this._addEventListeners[type].find(obj => obj.fn === fn)
    _removeListenerObject(this, type, obj)
  }

  close () {
    if (this.closed) {
      return
    }
    this.closed = true
    const awaitPrepare = this._preparePromises ? this._preparePromises : Promise.resolve()

    this._onMessageListener = null
    this._addEventListeners.message = []

    return awaitPrepare
      // Wait until all current sending are processed
      .then(() => Promise.all(Array.from(this._unSendMessagePromises)))
      // Run before-close hooks
      .then(() => Promise.all(this._beforeClose.map(fn => fn())))
      // Close the channel
      .then(() => this.method.close(this._state))
  }

  get type () {
    return this.method.type
  }

  isClosed () {
    return this.closed
  }
}

function _post (messageChannel, type, msg) {
  const time = messageChannel.method.microSeconds()
  const msgObj = {
    time,
    type,
    data: msg
  }

  const awaitPrepare = messageChannel._preparePromises ? messageChannel._preparePromises : Promise.resolve()
  return awaitPrepare.then(() => {
    const sendPromise = messageChannel.method.postMessage(
      messageChannel._state,
      msgObj
    )

    // add/remove to un-send messages list
    messageChannel._unSendMessagePromises.add(sendPromise)
    sendPromise
      .catch()
      .then(() => messageChannel._unSendMessagePromises.delete(sendPromise))

    return sendPromise
  })
}

function _prepareChannel (channel) {
  const maybePromise = channel.method.create(channel.name, channel.options)
  /* istanbul ignore next */
  if (isPromise(maybePromise)) {
    channel._preparePromises = maybePromise
    maybePromise.then(s => {
      channel._state = s
    })
  } else {
    channel._state = maybePromise
  }
}

function _hasMessageListeners (channel) {
  if (channel._addEventListeners.message.length > 0) return true
  if (channel._addEventListeners.internal.length > 0) return true
  return false
}

function _addListenerObject (channel, type, obj) {
  channel._addEventListeners[type].push(obj)
  _startListening(channel)
}

function _removeListenerObject (channel, type, obj) {
  channel._addEventListeners[type] = channel._addEventListeners[type].filter(o => o !== obj)
  _stopListening(channel)
}

function _startListening (channel) {
  if (!channel._isListening && _hasMessageListeners(channel)) {
    // someone is listening, start subscribing

    const listenerFn = msgObj => {
      channel._addEventListeners[msgObj.type].forEach(obj => {
        if (msgObj.time >= obj.time) {
          obj.fn(msgObj.data)
        }
      })
    }

    const time = channel.method.microSeconds()
    if (channel._preparePromises) {
      /* istanbul ignore next */
      channel._preparePromises.then(() => {
        channel._isListening = true
        channel.method.onMessage(
          channel._state,
          listenerFn,
          time
        )
      })
    } else {
      channel._isListening = true
      channel.method.onMessage(
        channel._state,
        listenerFn,
        time
      )
    }
  }
}

function _stopListening (channel) {
  if (channel._isListening && !_hasMessageListeners(channel)) {
    // no one is listening, stop subscribing
    channel._isListening = false
    const time = channel.method.microSeconds()
    channel.method.onMessage(
      channel._state,
      null,
      time
    )
  }
}

import { IS_BROWSER, sleep, randomToken } from '../utils'

class LeaderElection {
  constructor (channel, options) {
    this._channel = channel
    this._options = options

    this.isLeader = false
    this.isDead = false
    this.token = randomToken()

    this._isApplying = false
    this._reApply = false

    // things to clean up
    this._unloadFns = []
    this._listeners = []
    this._intervals = []
    this._duplicateListeners = () => { }
    this._duplicateCalled = false
    this._onBeforeDie = async () => {}

    const unloadFn = async () => this.die()

    if (IS_BROWSER) {
      window.addEventListener('beforeUnload', unloadFn)
      window.addEventListener('unload', unloadFn)

      this._unloadFns.push(['beforeUnload', unloadFn])
      this._unloadFns.push(['unload', unloadFn])
    }
  }

  applyOnce () {
    if (this.isLeader) return Promise.resolve(false)
    if (this.isDead) return Promise.resolve(false)

    // do nothing if already running
    if (this._isApplying) {
      this._reApply = true
      return Promise.resolve(false)
    }
    this._isApplying = true

    let stopCriteria = false
    const received = []

    const handleMessage = (msg) => {
      if (msg.context === 'leader' && msg.token !== this.token) {
        received.push(msg)

        if (msg.action === 'apply') {
          // Other is applying
          if (msg.token > this.token) {
            // Other has higher token, stop applying
            stopCriteria = true
          }
        }

        if (msg.action === 'tell') {
          // Other is already leader
          stopCriteria = true
        }
      }
    }
    this._channel.addEventListener('internal', handleMessage)

    return _sendMessage(this, 'apply') // send out that this one is applying
      .then(() => sleep(this._options.responseTime)) // let others time to respond
      .then(() => {
        if (stopCriteria) return Promise.reject(new Error())
        else return _sendMessage(this, 'apply')
      })
      .then(() => sleep(this._options.responseTime)) // let others time to respond
      .then(() => {
        if (stopCriteria) return Promise.reject(new Error())
        else return _sendMessage(this)
      })
      .then(() => beLeader(this)) // no one disagreed -> this one is now leader
      .then(() => true)
      .catch(() => false) // apply not successful
      .then(success => {
        this._channel.removeEventListener('internal', handleMessage)
        this._isApplying = false
        if (!success && this._reApply) {
          this._reApply = false
          return this.applyOnce()
        } else return success
      })
  }

  awaitLeadership () {
    if (
      !this._awaitLeadershipPromise
    ) {
      this._awaitLeadershipPromise = _awaitLeadershipOnce(this)
    }
    return this._awaitLeadershipPromise
  }

  set onDuplicate (fn) {
    this._duplicateListeners = fn
  }

  /* istanbul ignore next */
  get onDuplicate () {
    return this._duplicateListeners
  }

  set onBeforeDie (fn) {
    this._onBeforeDie = fn
  }

  /* istanbul ignore next */
  get onBeforeDie () {
    return this._onBeforeDie
  }

  async die () {
    if (this.isDead) return
    this.isDead = true

    await this.onBeforeDie()
    this._listeners.forEach(listener => this._channel.removeEventListener('internal', listener))
    this._intervals.forEach(interval => clearInterval(interval))
    this._unloadFns.forEach(uFn => {
      if (IS_BROWSER) {
        window.removeEventListener(uFn[0], uFn[1])
      }
    })
    return _sendMessage(this, 'death')
  }
}

function _awaitLeadershipOnce (leaderElector) {
  if (leaderElector.isLeader) return Promise.resolve()

  return new Promise(resolve => {
    let resolved = false

    function finish () {
      /* istanbul ignore next */
      if (resolved) {
        return
      }
      resolved = true
      clearInterval(interval)
      leaderElector._channel.removeEventListener('internal', whenDeathListener)
      resolve(true)
    }

    // try once now
    leaderElector.applyOnce().then(() => {
      if (leaderElector.isLeader) {
        finish()
      }
    })

    // try on fallbackInterval
    const interval = setInterval(() => {
      /* istanbul ignore next */
      leaderElector.applyOnce().then(() => {
        if (leaderElector.isLeader) {
          finish()
        }
      })
    }, leaderElector._options.fallbackInterval)
    leaderElector._intervals.push(interval)

    // try when other leader dies
    const whenDeathListener = msg => {
      if (msg.context === 'leader' && msg.action === 'death') {
        leaderElector.applyOnce().then(() => {
          if (leaderElector.isLeader) finish()
        })
      }
    }
    leaderElector._channel.addEventListener('internal', whenDeathListener)
    leaderElector._listeners.push(whenDeathListener)
  })
}

/**
 * Sends and internal message over the broadcast-channel
 */
function _sendMessage (leaderElector, action) {
  const msgJson = {
    context: 'leader',
    action,
    token: leaderElector.token
  }
  return leaderElector._channel.postInternal(msgJson)
}

export function beLeader (leaderElector) {
  leaderElector.isLeader = true

  const isLeaderListener = msg => {
    if (msg.context === 'leader' && msg.action === 'apply') {
      _sendMessage(leaderElector, 'tell')
    }

    if (msg.context === 'leader' && msg.action === 'tell' && !leaderElector._duplicateCalled) {
      /**
       * Another instance is also leader!
       * This can happen on rare events
       * like when the CPU is at 100% for long time
       * or the tabs are open very long and the browser throttles them.
       */
      leaderElector._duplicateCalled = true
      leaderElector._duplicateListeners() // message the lib user so the app can handle the problem
      _sendMessage(leaderElector, 'tell') // ensure other leader also knows the problem
    }
  }
  leaderElector._channel.addEventListener('internal', isLeaderListener)
  leaderElector._listeners.push(isLeaderListener)
  return _sendMessage(leaderElector, 'tell')
}

export function createLeaderElection (channel, options) {
  if (channel._leaderElector) {
    throw new Error('❌ MessageChannel already has a leader-elector')
  }

  const elector = new LeaderElection(channel, options)
  channel._beforeClose.push(async () => elector.die())

  channel._leaderElector = elector
  return elector
}

import { MessageChannel, createLeaderElection } from './MessageChannel'
export const TabManager = ({
  type,
  channelName,
  fallbackInterval,
  responseTime,
  emitOnAllTabs,
  onIdle,
  onActive,
  start,
  reset,
  pause,
  resume
}) => {
  const channel = new MessageChannel(channelName, { type })
  const elector = createLeaderElection(channel, { fallbackInterval, responseTime })
  const registry = {}

  // Register self
  registry[elector.token] = false

  let leader = false
  let allIdle = true

  const isLeader = () => leader

  elector.awaitLeadership().then(() => {
    leader = true
  })

  channel.addEventListener('message', ([type, id]) => {
    switch (type) {
      case 'register':
        registry[id] = false
        break
      case 'deregister':
        delete registry[id]
        break
      case 'idle':
        idle(id)
        break
      case 'active':
        active(id)
        break
      case 'emitIdle':
        onIdle()
        break
      case 'emitActive':
        onActive()
        break
      case 'start':
        start(true)
        break
      case 'reset':
        reset(true)
        break
      case 'pause':
        pause(true)
        break
      case 'resume':
        resume(true)
        break
      default:
        // no op
    }
  })

  const setAllIdle = bool => {
    allIdle = bool
  }

  const isAllIdle = () => allIdle

  const idle = (id = elector.token) => {
    registry[id] = true
    const isIdle = Object.values(registry).every(v => v)
    if (!allIdle && isIdle) {
      allIdle = true
      if (isLeader()) {
        onIdle()
        if (emitOnAllTabs) send('emitIdle')
      } else {
        send('idle')
      }
    }
  }

  const active = (id = elector.token) => {
    registry[id] = false
    const isActive = Object.values(registry).some(v => !v)
    if (allIdle && isActive) {
      allIdle = false
      if (isLeader()) {
        onActive()
        if (emitOnAllTabs) send('emitActive')
      } else {
        send('active')
      }
    }
  }

  /* istanbul ignore next */
  elector.onDuplicate = async () => await elector.die()
  elector.onBeforeDie = async () => await send('deregister')

  const send = async message => channel.postMessage([message, elector.token])

  const close = async () => {
    await elector.die()
    await channel.close()
  }

  // Register self with remote tabs
  send('register')

  return { close, send, isLeader, idle, active, isAllIdle, setAllIdle }
}

/**
 *  ___    _ _     _____ _
 * |_ _|__| | | __|_   _(_)_ __ ___   ___ _ __
 *  | |/ _` | |/ _ \| | | | '_ ` _ \ / _ \ '__|
 *  | | (_| | |  __/| | | | | | | | |  __/ |
 * |___\__,_|_|\___||_| |_|_| |_| |_|\___|_|
 *
 * @name IdleTimer
 * @author Randy Lebeau
 * @private
 */

import { Component } from 'react'
import PropTypes from 'prop-types'
import { TabManager } from './TabManager'
import { IS_BROWSER, DEFAULT_ELEMENT, DEFAULT_EVENTS, debounced, throttled } from './utils'

/**
 * Detects when your user is idle
 * @class IdleTimer
 * @private
 */
class IdleTimer extends Component {
  /**
   * Creates an instance of IdleTimer
   * bind all of our internal events here
   * for best performance
   * @param {Object} props
   * @return {IdleTimer}
   * @private
   */
  constructor (props) {
    super(props)

    /**
     * Sets initial component state
     * @type {Object}
     * @private
     */
    this.state = {
      idle: false,
      oldDate: +new Date(),
      lastActive: +new Date(),
      lastIdle: null,
      idleTime: 0,
      remaining: null,
      pageX: null,
      pageY: null
    }

    /**
     * The timer instance
     * @type {Timeout}
     * @private
     */
    this.tId = null

    /**
     * Wether or not events are bound
     * @type {boolean}
     * @private
     */
    this.eventsBound = false

    // Debounce and throttle can't both be set
    if (props.debounce > 0 && props.throttle > 0) {
      throw new Error('onAction can either be throttled or debounced (not both)')
    }

    // Create debounced action if applicable
    if (props.debounce > 0) {
      this._onAction = debounced(props.onAction, props.debounce)

    // Create throttled action if applicable
    } else if (props.throttle > 0) {
      this._onAction = throttled(props.onAction, props.throttle)

    // Set custom onAction
    } else {
      this._onAction = props.onAction
    }

    // Create a throttle event handler if applicable
    if (props.eventsThrottle > 0) {
      this.handleEvent = throttled(this._handleEvent.bind(this), props.eventsThrottle)
    } else {
      this.handleEvent = this._handleEvent.bind(this)
    }

    // If startOnMount is set, idle state defaults to true
    if (!props.startOnMount || props.startManually) {
      this.state.idle = true
    }

    // Bind all events to component scope, built for speed 🚀
    this._toggleIdleState = this._toggleIdleState.bind(this)
    this.start = this.start.bind(this)
    this.reset = this.reset.bind(this)
    this.pause = this.pause.bind(this)
    this.resume = this.resume.bind(this)
    this.isIdle = this.isIdle.bind(this)
    this.getRemainingTime = this.getRemainingTime.bind(this)
    this.getElapsedTime = this.getElapsedTime.bind(this)
    this.getLastActiveTime = this.getLastActiveTime.bind(this)
    this.getLastIdleTime = this.getLastIdleTime.bind(this)
    this.getTotalIdleTime = this.getTotalIdleTime.bind(this)
    this.getTotalActiveTime = this.getTotalActiveTime.bind(this)
  }

  /**
   * Runs once the component has mounted
   * here we handle automatically starting
   * the idletimer
   * @private
   */
  componentDidMount () {
    const { startOnMount, startManually, crossTab, onIdle, onActive } = this.props

    // Set up cross tab
    /* istanbul ignore next */
    if (crossTab) {
      const {
        type,
        channelName,
        fallbackInterval,
        responseTime,
        emitOnAllTabs
      } = Object.assign({
        channelName: 'idle-timer',
        fallbackInterval: 2000,
        responseTime: 100,
        removeTimeout: 1000 * 60,
        emitOnAllTabs: false
      }, crossTab === true ? {} : crossTab)

      this.manager = TabManager({
        type,
        channelName,
        fallbackInterval,
        responseTime,
        emitOnAllTabs,
        onIdle,
        onActive,
        start: this.start,
        reset: this.reset,
        pause: this.pause,
        resume: this.resume
      })
    }

    if (startManually) return
    if (startOnMount) {
      this.start()
    } else {
      this._bindEvents()
    }
  }

  componentDidUpdate (prevProps) {
    // Update debounce function
    if ((prevProps.debounce !== this.props.debounce) && this.props.debounce > 0) {
      if (this._onAction.cancel) this._onAction.cancel()
      this._onAction = debounced(this.props.onAction, this.props.debounce)
    } else
    // Update throttle function
    if ((prevProps.throttle !== this.props.throttle) && this.props.throttle > 0) {
      if (this._onAction.cancel) this._onAction.cancel()
      this._onAction = throttled(this.props.onAction, this.props.throttle)
    } else
    // Remove throttle or debounce
    if (
      (prevProps.throttle && this.props.throttle === 0) ||
      (prevProps.debounce && this.props.debounce === 0)
    ) {
      if (this._onAction.cancel) this._onAction.cancel()
      this._onAction = this.props.onAction
    }

    // Update event throttle function
    if (prevProps.eventsThrottle !== this.props.eventsThrottle) {
      this._unbindEvents()
      this.handleEvent = throttled(this._handleEvent.bind(this), this.props.eventsThrottle)
      this._bindEvents()
    }
    // Update timeout value
    if (prevProps.timeout !== this.props.timeout) {
      if (this.state.idle) this.reset()
    }
  }

  /**
   * Called before the component un-mounts
   * here we clear the timer and remove
   * all the event listeners
   * @private
   */
  componentWillUnmount () {
    // Clear timeout to prevent delayed state changes
    clearTimeout(this.tId)
    this._unbindEvents(true)
    // Cancel any debounced onAction handlers
    if (this._onAction.cancel) this._onAction.cancel()
    /* istanbul ignore next */
    if (this.manager) {
      this.manager.close().catch(console.error)
    }
  }

  /**
   * Render children if IdleTimer is used as a wrapper
   * @return {Component} children
   * @private
   */
  render () {
    const { children } = this.props
    return children || null
  }

  /**
   * Binds the specified events
   * @private
   */
  _bindEvents () {
    // Don't bind events if
    // we are not in a browser
    if (!IS_BROWSER) return
    // Otherwise we bind all the events
    // to the supplied element
    const { element, events, passive, capture } = this.props
    if (!this.eventsBound) {
      events.forEach(e => {
        element.addEventListener(e, this.handleEvent, {
          capture,
          passive
        })
      })
      this.eventsBound = true
    }
  }

  /**
   * Unbinds all the bound events
   * @private
   */
  _unbindEvents (force = false) {
    // If we are not in a browser
    // we don't need to unbind events
    if (!IS_BROWSER) return
    // Unbind all events
    const { element, events, passive, capture } = this.props
    if (this.eventsBound || force) {
      events.forEach(e => {
        element.removeEventListener(e, this.handleEvent, {
          capture,
          passive
        })
      })
      this.eventsBound = false
    }
  }

  /**
   * Toggles the idle state and calls
   * the correct action function
   * @private
   */
  _toggleIdleState (e) {
    // Fire the appropriate action
    // and pass the event through
    // Toggle the idle state
    this.setState((prevState) => ({
      idle: !prevState.idle,
      lastIdle: !prevState.idle ? (+new Date()) - this.props.timeout : prevState.lastIdle,
      idleTime: prevState.idle ? prevState.idleTime + (+new Date()) - prevState.lastIdle : prevState.idleTime
    }), () => {
      const { onActive, onIdle, stopOnIdle } = this.props
      const { idle } = this.state
      if (idle) {
        if (stopOnIdle) {
          // Clear any existing timeout
          clearTimeout(this.tId)
          this.tId = null
          // Unbind events
          this._unbindEvents()
        }

        if (this.manager) {
          /* istanbul ignore next */
          this.manager.idle()
        } else {
          onIdle(e)
        }
      } else {
        this._bindEvents()
        if (this.manager) {
          /* istanbul ignore next */
          this.manager.active()
        } else {
          onActive(e)
        }
      }
    })
  }

  /**
   * Event handler for supported event types
   * @param  {Object} e event object
   * @private
   */
  _handleEvent (e) {
    const { remaining, pageX, pageY, idle } = this.state
    const { timeout, stopOnIdle } = this.props

    // Fire onAction event
    this._onAction(e)

    // Already active, ignore events
    if (remaining) return

    // Mousemove event
    if (e.type === 'mousemove') {
      // If coords are same, it didn't move
      /* istanbul ignore next */
      if (e.pageX === pageX && e.pageY === pageY) {
        return
      }
      // If coords don't exist how could it move
      if (typeof e.pageX === 'undefined' && typeof e.pageY === 'undefined') {
        return
      }
      // Under 200 ms is hard to do
      // continuous activity will bypass this
      /* istanbul ignore next */
      const elapsed = this.getElapsedTime()
      /* istanbul ignore next */
      if (elapsed < 200) {
        return
      }
    }

    // Clear any existing timeout
    clearTimeout(this.tId)
    this.tId = null

    // Determine last time User was active, as can't rely on setTimeout ticking at the correct interval
    const elapsedTimeSinceLastActive = +new Date() - this.getLastActiveTime()

    // If the user is idle or last active time is more than timeout, flip the idle state
    if ((idle && !stopOnIdle) || (!idle && elapsedTimeSinceLastActive > timeout)) {
      this._toggleIdleState(e)
    }

    // Store when the user was last active
    // and update the mouse coordinates
    this.setState({
      lastActive: +new Date(),
      pageX: e.pageX,
      pageY: e.pageY
    })

    // If the user is idle and stopOnIdle flag is not set
    // set a new timeout
    if (idle) {
      if (!stopOnIdle) {
        this.tId = setTimeout(this._toggleIdleState, timeout)
      }
    } else {
      this.tId = setTimeout(this._toggleIdleState, timeout)
    }
  }

  /**
   * Set initial state and start timer
   * @name start
   */
  start (remote = true) {
    // Clear timeout
    clearTimeout(this.tId)
    this.tId = null

    // Bind the events
    this._bindEvents()

    // Reset state
    this.setState({
      idle: false,
      oldDate: +new Date(),
      lastActive: +new Date(),
      remaining: null
    })

    if (this.manager) {
      /* istanbul ignore next */
      this.manager.setAllIdle(false)
      /* istanbul ignore next */
      if (!remote && this.props.crossTab.emitOnAllTabs) {
        this.manager.send('start')
      }
    }

    // Set new timeout
    const { timeout } = this.props
    this.tId = setTimeout(this._toggleIdleState, timeout)
  }

  /**
   * Restore initial state and restart timer, calling onActive
   * @name reset
   */
  reset (remote = false) {
    // Clear timeout
    clearTimeout(this.tId)
    this.tId = null

    // Bind the events
    this._bindEvents()

    if (this.state.idle) this.props.onActive()

    if (this.manager) {
      /* istanbul ignore next */
      this.manager.setAllIdle(false)
      /* istanbul ignore next */
      if (!remote && this.props.crossTab.emitOnAllTabs) {
        this.manager.send('reset')
      }
    }

    // Reset state
    this.setState({
      idle: false,
      oldDate: +new Date(),
      lastActive: +new Date(),
      remaining: null
    })

    // Set new timeout
    const { timeout } = this.props
    this.tId = setTimeout(this._toggleIdleState, timeout)
  }

  /**
   * Store remaining time and stop timer
   * @name pause
   */
  pause (remote = false) {
    // Timer is already paused
    const { remaining } = this.state
    if (remaining !== null) return

    // Unbind events
    this._unbindEvents()

    // Clear existing timeout
    clearTimeout(this.tId)
    this.tId = null

    // Send event to other tabs
    if (this.manager) {
      /* istanbul ignore next */
      if (!remote && this.props.crossTab.emitOnAllTabs) {
        this.manager.send('pause')
      }
    }

    // Define how much is left on the timer
    this.setState({
      remaining: this.getRemainingTime()
    })
  }

  /**
   * Resumes a paused timer
   * @name resume
   */
  resume (remote = false) {
    // Timer is not paused
    const { remaining, idle } = this.state
    if (remaining === null) return

    // Bind events
    this._bindEvents()

    // Send event to other tabs
    if (this.manager) {
      /* istanbul ignore next */
      if (!remote && this.props.crossTab.emitOnAllTabs) {
        this.manager.send('resume')
      }
    }

    // Start timer and clear remaining
    // if we are in the active state
    if (!idle) {
      // Set a new timeout
      this.tId = setTimeout(this._toggleIdleState, remaining)
      // Set new state
      this.setState({ remaining: null, lastActive: +new Date() })
    }
  }

  /**
   * Time remaining before idle
   * @name getRemainingTime
   * @return {number} Milliseconds remaining
   */
  getRemainingTime () {
    const { remaining, lastActive } = this.state
    const { timeout } = this.props

    // If idle there is no time remaining
    if (remaining !== null) {
      return remaining < 0 ? 0 : remaining
    }

    // Determine remaining, if negative idle didn't finish flipping, just return 0
    const timeLeft = timeout - ((+new Date()) - lastActive)
    return timeLeft < 0 ? 0 : timeLeft
  }

  /**
   * How much time has elapsed
   * @name getElapsedTime
   * @return {Timestamp}
   */
  getElapsedTime () {
    const { oldDate } = this.state
    return (+new Date()) - oldDate
  }

  /**
   * Last time the user was idle
   * @name getLastIdleTime
   * @return {Timestamp}
   */
  getLastIdleTime () {
    const { lastIdle } = this.state
    return lastIdle
  }

  /**
   * Total time the user was idle
   * @name getTotalIdleTime
   * @return {number}
   */
  getTotalIdleTime () {
    const { idle, lastIdle, idleTime } = this.state
    if (idle) {
      return ((+new Date()) - lastIdle) + idleTime
    } else {
      return idleTime
    }
  }

  /**
   * Last time the user was active
   * @name getLastActiveTime
   * @return {Timestamp}
   */
  getLastActiveTime () {
    const { lastActive } = this.state
    return lastActive
  }

  /**
   * Total time the user was active
   * @name getTotalActiveTime
   * @return {number}
   */
  getTotalActiveTime () {
    return this.getElapsedTime() - this.getTotalIdleTime()
  }

  /**
   * Returns wether or not the user is idle
   * @name isIdle
   * @return {boolean}
   */
  isIdle () {
    const { idle } = this.state
    return idle
  }

  /**
   * Returns wether or not this is the leader tab
   * @name isLeader
   * @return {boolean}
   */
  isLeader () {
    return this.manager ? this.manager.isLeader() : true
  }
}

