import React, { useState } from 'react'
import { ChromePicker } from 'react-color'
import './App.css';

function App() {
  const [color, setColor] = useState('#fff')

  return (
    <div className="App">
      <ChromePicker 
        color={color} 
        onChange={updatedColor => setColor(updatedColor)}
      />
      <h2>You picked (</h2>
    </div>
  );
}

export default App;
