import React from 'react'
import { useFormik } from 'formik'
import * as Yup from 'yup'

//Managing the form state
const initialValues = {
    name: '',
    email: '',
    channel: ''
}

//Handling form submission
const onSubmit = values => {
    console.log(" Form Data", values);
}

//Form Validation 
